mapper.select:
SELECT count(*)
FROM reply
WHERE re_parent_no = #{reParentNo}
  AND re_category = #{reCategory}

mapper.update:
UPDATE reply
SET re_content = #{reContent},
    re_mod_date = sysdate
WHERE re_no = #{reNo}
  AND re_mem_id = #{reMemId}

mapper.delete:
DELETE FROM reply
WHERE re_no = #{reNo}
AND re_mem_id = #{reMemId}
